mapper.select:
select id, user_id, title, content, create_time, view
from free_board
where id = #{id};

select board_id, count(board_id) as number
from board_like
group by board_id;

select id, board_id, user_id, comment, create_time
from free_comment
where board_id = #{boardId};

mapper.update:
update free_board set title = #{title}, content = #{content}, create_time = now()
where id = #{id}
and user_id = #{userId};

mapper.delete:
delete from free_board
where id = #{id}
and user_id = #{userId};

delete from board_like
where board_id = #{boardId}
and user_id = #{userId};

mapper.update:
update free_comment set comment = #{comment}, create_time = now()
where id = #{id}
and user_id = #{userId};

mapper.delete:
delete from free_comment
where id = #{id}
and user_id = #{userId};
