mapper.select:
select id, user_id, title, content, create_time, view, total_like
from free_board
where id = #{id};

select id, board_id, user_id, comment, create_time
from free_comment
where board_id = #{boardId};

select count(board_id)
from board_like
where board_id = #{boardId}
and user_id = #{userId};

mapper.update:
update free_board set title = #{title}, content = #{content}, create_time = now()
where id = #{id}
and user_id = #{userId};

mapper.delete:
delete from free_board
where id = #{id}
and user_id = #{userId};

delete from board_like
where board_id = #{boardId}
and user_id = #{userId};

mapper.update:
update free_comment set comment = #{comment}, create_time = now()
where id = #{id}
and user_id = #{userId};

mapper.delete:
delete from free_comment
where id = #{id}
and user_id = #{userId};
