mapper.select:
select id, user_id, title, content, create_time
from notice_board
where id = #{id}

mapper.update:
update notice_board set title = #{title}, content = #{content}, create_time = now()
where id = #{id}
and user_id = #{userId};

mapper.delete:
delete from notice_board
where id = #{id}
and user_id = #{userId};
