mapper.select:
select id, title, create_time
from qna_board
where user_id = #{userId};

select id, title, content, create_time
from qna_board
where id = #{id};

select id, qna_id, comment, create_time 
from qna_comment
where qna_id = #{id};

mapper.update:
update qna_board set title = #{title}, content = #{content}, create_time = now()
where id = #{id}
and user_id = #{userId};

mapper.delete:
delete from qna_board
where id = #{id}
and user_id = #{userId};
