mapper.select:
select id, board_id, user_id, comment, create_time, good 
from free_comment
where board_id = #{boardId};

mapper.update:
update free_board set title = #{title}, content = #{content}, create_time = now()
where id = #{id}
and user_id = #{userId};

mapper.delete:
delete from free_board
where id = #{id}
and user_id = #{userId};

mapper.update:
update free_comment set comment = #{comment}, create_time = now()
where id = #{id}
and user_id = #{userId};

mapper.delete:
delete from free_comment
where id = #{id}
and user_id = #{userId};
